CREATE TABLE `templates` (`id` integer not null primary key autoincrement, `title` varchar(255) not null, `description` text not null, `word_file_path` varchar(255) not null, `email_template_path` varchar(255) not null, `uploaded_by` varchar(255) not null, `uploaded_at` datetime default CURRENT_TIMESTAMP, `updated_at` datetime default CURRENT_TIMESTAMP, `deleted_at` datetime null);